async function getFilesPHP(t=1){const e=await fetch("src/scripts/view.pic.php"),i=await e.json();let n,s=[];for(let e=0;e<t;e++)n=Math.floor(Math.random()*i.length),s.push(i[n]);return s}const filesPHP=getFilesPHP(100);filesPHP.then(t=>{for(let e=0;e<t.length;e++){const i=document.createElement("div");i.classList.add("picture-container"),i.innerHTML=`<div class="pic-overlay">\n\n                <div class="top left button">save on list</div>\n                <div class="top right button">quick-save</div>\n                <div class="bottom right button">extend</div>\n\n        </div>\n        <a href="#"><img src="./src/images/posts/${t[e]}" width="200" height="450" alt="autoloaded content">`,document.getElementById("view-pictures").appendChild(i)}});